mapper.select:
select id, order_id orderId, org_ids orgIds, plan_charge_amount planChargeAmount, actual_charge_amount actualChargeAmount, status, discount_type discountType, discount_id discountId, pay_type payType, pay_method payMethod, store_no storeNo, parent_store_no parentStoreNo, org_id orgId, user_id userId, utime, ctime from tb_order
ctime >= #{startTime} and ctime <= #{endTime}
and pay_type = #{payType}
and pay_method = #{payMethod}
and status = #{status}
and plan_charge_amount >= #{startAmount}
and plan_charge_amount <= #{endAmount}
and order_id = #{orderId}
and org_ids like "${orgIds}%"
limit #{start},#{pageSize}

select
  count(id) 
 from tb_order

         ctime >= #{startTime}
        and  ctime  <= #{endTime}

        and pay_type = #{payType}


        and pay_method = #{payMethod}


        and status = #{status}


        and plan_charge_amount >= #{startAmount}


        and plan_charge_amount  <= #{endAmount}


        and order_id = #{orderId}


       and org_ids like "${orgIds}%"

       select
         id,
         order_id orderId,
         org_ids orgIds,
	     plan_charge_amount planChargeAmount,
	     actual_charge_amount actualChargeAmount,
	     status,
	     discount_type discountType,
	     discount_id discountId,
	     pay_type payType,
	     pay_method payMethod,
	     store_no storeNo,
	     parent_store_no parentStoreNo,
	     org_id orgId,
	     user_id userId,
	     utime,
	     ctime  
       from tb_order

               ctime >= #{startTime}
              and  ctime  <= #{endTime}

              and pay_type = #{payType}


              and pay_method = #{payMethod}


              and status = #{status}


              and plan_charge_amount >= #{startAmount}


              and plan_charge_amount  <= #{endAmount}


              and order_id = #{orderId}


              and store_no = #{storeNo} or parent_store_no = #{storeNo}


       limit #{start},#{pageSize}

select
   count(id)
